mapper.select:
SELECT t1.* FROM crm_ad_goods_order t1,hr_office office,crm_depot depot WHERE t1.enabled=1 and t1.shop_id=depot.id and depot.office_id=office.id
and t1.bill_type =#{p.billType}
and t1.process_status =#{p.processStatus}
and t1.remarks like concat('%',#{p.remarks},'%')
and t1.created_date >= #{p.createdDateStart}
and t1.created_date <= #{p.createdDateEnd}
and t1.bill_date >= #{p.billDateStart}
and t1.bill_date <= #{p.billDateEnd}
and t1.created_by in( select id from hr_account where login_name like concat('%',#{p.createdBy},'%') and enabled=1 )
and t1.created_by in ( select t2.id from hr_account t2 , hr_account_office t3, hr_office t4 where t2.id = t3.account_id and t3.office_id = t4.id and t4.name like CONCAT('%',#{p.officeName},'%') )
AND t1.store_id =#{p.storeId} )
and t1.shop_id in( select depot.id from crm_depot depot where depot.name like concat('%',#{p.shopName},'%') and depot.enabled=1 )
and t1.shop_id in( select depot.id from crm_depot depot where depot.area_type like concat('%',#{p.areaType},'%') and depot.enabled=1 )
