mapper.select:
 select t1.*
from crm_after_sale_ime_allot t1
where t1.product_ime_id = #{productImeId}

select t1.*
from crm_after_sale_ime_allot t1
where t1.after_sale_id = #{afterSaleId}

SELECT t1.* FROM crm_after_sale_ime_allot t1 WHERE t1.enabled=1
AND t1.from_depot_id in( select depot.id FROM crm_depot depot where depot.enabled=1 and depot.name like concat('%',#{p.fromDepotName},'%') )
AND t1.to_depot_id in( select depot.id FROM crm_depot depot where depot.enabled=1 and depot.name like concat('%',#{p.toDepotName},'%') )
and t1.created_date >= #{p.createdDateStart}
and t1.created_date <= #{p.createdDateEnd}
and t1.created_by in( select id from hr_account where login_name like concat('%',#{p.createdBy},'%') and endabled=1 )
