mapper.select:
select t1.* from crm_product_ime_upload t1,crm_depot depot,hr_office office where t1.enabled=1 and t1.shop_id=depot.id and depot.office_id=office.id
and t1.created_date >=#{p.createdDateStart}
and t1.created_date <=#{p.createdDateEnd}
and depot.name LIKE CONCAT('%',#{p.shopName},'%')
and office.id = #{p.officeId}

select t1.*
from crm_product_ime_upload t1
where t1.product_ime_id = #{productImeId}
