mapper.select:
SELECT t1.* FROM crm_shop_deposit t1,crm_depot depot WHERE t1.enabled=1 and t1.shop_id = depot.id
AND depot.name LIKE CONCAT('%',#{p.shopName},'%')
AND t1.type =#{p.type}
AND t1.remarks LIKE CONCAT('%',#{p.remarks},'%')
and t1.created_date >=#{p.createdDateStart}
and t1.created_date <=#{p.createdDateEnd}
and t1.created_by in( select id from hr_account where login_name like concat('%',#{p.createdBy},'%') and enabled=1 )

SELECT t1.* FROM crm_shop_deposit t1 where t1.id in ( SELECT Max(id) FROM crm_shop_deposit where `type`=#{type}
and shop_id IN
#{item}
GROUP BY shop_id,type )

SELECT
t1.*
FROM
crm_shop_deposit t1
where t1.shop_id = #{shopId}
and t1.type = #{type}
and t1.enabled =1
order by t1.created_date desc  LIMIT 0,#{size}
