mapper.select:
SELECT b.FRETCUSTID AS customerId, sum(c.FAMOUNT) AS beginShouldGet FROM T_SAL_RETURNSTOCKENTRY a JOIN T_SAL_RETURNSTOCK b ON a.FID = b.FID JOIN T_SAL_RETURNSTOCKENTRY_F c ON b.FID = c.FID and a.FENTRYID = c.FENTRYID JOIN T_BAS_BILLTYPE_L d ON d.FBILLTYPEID = b.FBILLTYPEID WHERE b.FDATE >=#{dateStart} and b.FDATE <=#{dateEnd} and d.FNAME= '标准销售退货单' and b.FRETCUSTID in
#{item}
group by b.FRETCUSTID

SELECT b.FRETCUSTID AS customerId, sum(c.FAMOUNT) AS beginShouldGet FROM T_SAL_RETURNSTOCKENTRY a JOIN T_SAL_RETURNSTOCK b ON a.FID = b.FID JOIN T_SAL_RETURNSTOCKENTRY_F c ON b.FID = c.FID and a.FENTRYID = c.FENTRYID JOIN T_BAS_BILLTYPE_L d ON d.FBILLTYPEID = b.FBILLTYPEID WHERE b.FDATE >=#{dateStart} and b.FDATE <=#{dateEnd} and d.FNAME='现销退货单' and b.FRETCUSTID in
#{item}
group by b.FRETCUSTID
