mapper.select:
SELECT FYEAR as year, FPERIOD as month, faccnumber as accNumber, faccname as accName, Fdeptnum as deptNum, Fdeptname as deptName, Ffynum as fyNum, Ffyname as fyName, SUM (famount) AS amount FROM glcx WHERE flx = '' AND Fyear = #{year} and Fperiod = #{month} AND faccname = #{accName}
AND Ffynum = #{fyNum}
GROUP BY FYEAR, FPERIOD, faccnumber, faccname, Fdeptnum, Fdeptname, Ffynum, Ffyname

SELECT faccnumber as accNumber, faccname as accName, Fdeptnum as deptNum, Fdeptname as deptName, Ffynum as fyNum, Ffyname as fyName, SUM (famount) AS amount FROM glcx WHERE flx = '' AND FYEAR*100+ FPERIOD BETWEEN #{dateStart} AND #{dateEnd} AND faccname = #{accName}
AND Ffynum = #{fyNum}
GROUP BY faccnumber, faccname, Fdeptnum, Fdeptname, Ffynum, Ffyname

SELECT FYEAR as year, FPERIOD as month, faccnumber as accNumber, faccname as accName, Ffynum as fyNum, Ffyname as fyName, SUM (famount) AS amount FROM glcx WHERE flx = '' AND Fyear = #{year} and Fperiod = #{month} AND faccname = #{accName}
AND Ffynum = #{fyNum}
GROUP BY FYEAR, FPERIOD, faccnumber, faccname, Ffynum, Ffyname

SELECT faccnumber as accNumber, faccname as accName, Ffynum as fyNum, Ffyname as fyName, SUM (famount) AS amount FROM glcx WHERE flx = '' AND FYEAR*100+ FPERIOD BETWEEN #{dateStart} AND #{dateEnd} AND faccname = #{accName}
AND Ffynum = #{fyNum}
GROUP BY faccnumber, faccname, Ffynum, Ffyname
