mapper.select:
select t1.*,account.office_id from hr_account_change t1,hr_account account,hr_office office where t1.enabled=1 and t1.account_id=account.id and account.office_id=office.id
AND t1.created_date>=#{p.createdDateStart}
AND t1.created_date <=#{p.createdDateEnd}
and t1.type = #{p.type}
and t1.account_id in( select t2.id from hr_account t2 where t2.login_name like concat('%',#{p.createdByName},'%') and t2.enabled=1 )
and t1.account_id in( select t2.id from hr_account t2 ,hr_office office where t2.enabled=1 and t2.office_id=office.id and ( office.parent_ids like concat('%,',#{p.officeId},',%') or office.id=#{p.officeId} ) )
