mapper.select:
SELECT t1.*,t3.office_id FROM hr_audit_file t1,hr_account t3,sys_office office WHERE t1.created_by=t3.id and t3.office_id=office.id AND t1.enabled=1
and t1.created_date >=#{p.createdDateStart}
and t1.created_date <=#{p.createdDateEnd}
and t1.process_type_id =#{p.processTypeId}
and t1.id=#{p.id}
and t1.title like concat('%',#{p.title},'%')
and t1.content like concat('%',#{p.content},'%')
and office.id=#{p.officeId}
and office.name like concat('%',#{p.officeName},'%')
and t1.created_by in( select id from hr_account where login_name like concat('%',#{p.createdByName},'%') and enabled=1 )
