mapper.select:
SELECT
t1.*,t2.name as 'processTypeName',t3.office_id
FROM hr_audit_file t1,hr_account t3,hr_office office,sys_process_type t2
WHERE
t1.created_by=t3.id
and t3.office_id=office.id
and t1.process_type_id=t2.id
AND t1.enabled=1


    and t1.created_date >=#{p.createdDateStart}


    and t1.created_date <=#{p.createdDateEnd}


    and t1.process_flow_id in(
    select id
    from sys_process_flow
    where 1=1

        and  position_id=#{p.positionId}


        and `name`=#{p.processflowName}

    )


    and t1.process_type_id =#{p.processTypeId}


    and t1.id=#{p.id}


    and t1.title like concat('%',#{p.title},'%')


    and t1.content like concat('%',#{p.content},'%')


    and office.id=#{p.officeId}


    and office.name like concat('%',#{p.officeName},'%')


    and t1.created_by in(
    select id
    from hr_account
    where login_name like concat('%',#{p.createdByName},'%')
    and enabled=1
    )
