SELECT
t1.*
FROM
hr_duty_free t1,
hr_account account,
sys_office office
WHERE
t1.created_by=account.id
and account.office_id=office.id
and t1.enabled=1

    AND t1.created_by=#{p.createdBy}


    AND t1.free_date >= #{p.freeDateStart}


    AND t1.free_date  <= #{p.freeDateEnd}


    AND t1.free_date >= #{p.dutyDateStart}


    AND t1.free_date  <= #{p.dutyDateEnd}

