t1.*
FROM
hr_duty_free t1
WHERE
t1.enabled=1
AND t1.free_date >= #{dateStart}
and t1.free_date <= #{dateEnd}
and t1.status in

    #{item}

SELECT fr.employee_id, fr.free_date as 'duty_date', fr.date_type FROM hr_duty_free fr WHERE fr.free_date gt;= #{dateStart} AND fr.free_date lt;= #{dateEnd}
and fr.employee_id in
#{item}

