SELECT t1.* FROM hr_duty_overtime t1, hr_account account, hr_office office WHERE t1.created_by=account.id and account.office_id=office.id and t1.enabled=1
AND t1.created_by=#{p.createdBy}
AND t1.duty_date>=#{p.dutyDateStart}
AND t1.duty_date <=#{p.dutyDateEnd}

SELECT
t1.*
FROM
hr_duty_overtime t1
WHERE
t1.enabled=1
and t1.employee_id=#{employeeId}
and t1.duty_date >= #{dateStart}
and t1.duty_date <= #{dateEnd}

SELECT
    t1.*
FROM
hr_duty_overtime t1
WHERE
t1.enabled=1
and t1.status=#{status}
and t1.employee_id=#{employeeId}
and t1.duty_date>=#{dutyDateStart}
and t1.duty_date <=#{dutyDateEnd}

SELECT
  t1.*
FROM
  hr_duty_overtime t1
  WHERE
  t1.enabled=1
  and t1.status=#{status}
  and t1.duty_date>=#{dateStart}
  and t1.duty_date <=#{dateEnd}
  and t1.employee_id=#{employeeId}

