SELECT
  t1.*
FROM
hr_duty_rest t1,
hr_account account,
hr_office office
WHERE
t1.created_by=account.id
and account.office_id=office.id
and t1.enabled=1

   AND t1.created_by=#{p.createdBy}


    AND t1.duty_date>=#{p.dutyDateStart}


    AND t1.duty_date <=#{p.dutyDateEnd}


    AND t1.type=#{p.type}


    AND t1.date_type=#{p.dateType}

