mapper.select:
SELECT
t1.*
FROM
crm_shop_build t1,crm_depot depot,hr_office office
WHERE
t1.enabled=1
and t1.shop_id=depot.id
and depot.office_id=office.id


    and t1.process_flow_id = #{p.processFlow}


    and t1.fixture_type=#{p.fixtureType}


    AND t1.shop_id in (
    select t2.id
    from crm_depot t2
    where t2.name LIKE CONCAT('%',#{p.shopName},'%')
    )


    and  t1.created_date >=#{p.createdDateStart}


    and  t1.created_date <=#{p.createdDateEnd}


    AND t1.created_by in(
    select t3.id
    from hr_account t3
    where t3.login_name like concat('%',#{p.createdBy},'%')
    )


   and t1.process_flow_id in (
        select t2.id
        from sys_process_flow t2
        where t2.position_id = #{p.positionId}
    )


    and office.id = #{p.officeId}
