mapper.select:
SELECT
depot.name shopName, bank.name bankName, depot.parent_id shopParentId, t1.*
FROM
crm_bank_in t1,crm_depot depot, crm_bank bank
WHERE
t1.enabled=1
and t1.shop_id=depot.id and t1.bank_id = bank.id


    and t1.id=#{p.id}


    and depot.name like concat('%',#{p.shopName},'%')



    and t1.amount=#{p.amount}


    and t1.out_code like concat('%',#{p.outCode},'%')


    and bank.name like concat('%',#{p.bankName},'%')



    and t1.process_status =#{p.processStatus}


    and t1.serial_number like concat('%',#{p.serialNumber},'%')


    and t1.created_date >= #{p.createdDateStart}


    and t1.created_date < #{p.createdDateEnd}


    and t1.bill_date >= #{p.billDateStart}


    and t1.bill_date < #{p.billDateEnd}


    and t1.input_date >= #{p.inputDateStart}


    and t1.input_date < #{p.inputDateEnd}
