mapper.select:
SELECT t1.*
FROM crm_goods_order_detail t1
WHERE t1.goods_order_id=#{goodsOrderId}

SELECT t1.*
 FROM crm_goods_order_detail t1,crm_goods_order t2 ,crm_depot t3
  where t3.enabled=1
  and t2.enabled=1
  and t1.goods_order_id=t2.id
  and t2.shop_id=t3.id
  and t3.area_id=#{areaId}
  and t2.created_date >= #{dateStart}
  and t2.created_date <= #{dateEnd}

SELECT t1.*
FROM crm_goods_order_detail t1,crm_goods_order t2 ,crm_depot t3
 where t3.enabled=1
 and t2.enabled=1
 and t1.goods_order_id=t2.id
 and t2.shop_id=t3.id
 and t3.area_id=#{areaId}
 and t2.bill_date >= #{dateStart}
 and t2.bill_date <= #{dateEnd}
 and t2.ship_type in

     #{item}
