mapper.select:
SELECT
    (
        SELECT
        sum(deposit.amount)
        FROM
        crm_shop_goods_deposit deposit
        WHERE
        deposit.shop_id = t1.shop_id
        AND deposit. STATUS = '已通过'
        AND deposit.enabled = 1
        ) totalShopGoodsDepositAmount,
    depot.client_id,
    depot.pricesystem_id,
    (select expressOrder.out_print_date from crm_express_order expressOrder where expressOrder.id = t1.express_order_id) expressOutPrintDate,
    (select expressOrder.express_print_date from crm_express_order expressOrder where expressOrder.id = t1.express_order_id) expressExpressPrintDate,
    t1.*
FROM
    crm_goods_order t1,
    crm_depot depot
WHERE
    t1.enabled = 1
    AND t1.shop_id = depot.id



    AND t1.net_type=#{p.netType}


    AND t1.business_id LIKE CONCAT('%',#{p.businessId},'%')


    AND t1.out_code LIKE CONCAT('%',#{p.outCode},'%')


    AND t1.store_id=#{p.storeId}


    AND t1.status=#{p.status}


    AND t1.remarks=#{p.remarks}


    AND t1.ship_type=#{p.shipType}


    AND depot.name LIKE CONCAT('%',#{p.shopName},'%')


    AND depot.area_id=#{p.areaId}


    AND t1.created_date >=#{p.createdDateStart}


    AND t1.created_date < #{p.createdDateEnd}


    AND t1.bill_date >=#{p.billDateStart}


    AND t1.bill_date < #{p.billDateEnd}


    AND t1.ship_date >=#{p.shipDateStart}


    AND t1.ship_date < #{p.shipDateEnd}


    and t1.created_by = #{p.createdBy}


    and t1.express_order_id in(
    select id
    from crm_express_order
    where express_codes like concat('%',#{p.expressCodes},'%')
    and enabled=1
    )

SELECT t1.*
from crm_goods_order t1
where t1.enabled=1
and t1.bill_date=#{billDate}
and t1.status=#{status}
and t1.store_id=#{storeId}

SELECT t1.*
FROM crm_goods_order t1
where t1.enabled=1
and t1.shop_id=#{shopId}
and t1.status=#{status}
