mapper.select:
select t1.*
from crm_product_ime_sale t1
where t1.product_ime_id = #{productImeId}

select t1.* from crm_product_ime_sale t1,crm_depot depot where t1.enabled=1 and t1.shop_id=depot.id
and t1.created_date >=#{p.createdDateStart}
and t1.created_date <=#{p.createdDateEnd}
and depot.name LIKE CONCAT('%',#{p.shopName},'%')
and t1.is_back = #{p.isBack}

select t1.*
FROM crm_product_ime_sale t1
WHERE t1.employee_id = #{employeeId}
and t1.created_date >= #{createdDateStart}
and t1.created_date <= #{createdDateEnd}

select t1.employee_id as name,count(*) as value
FROM crm_product_ime_sale t1
WHERE t1.employee_id IN

    #{item}

and t1.created_date >= #{createdDateStart}
and t1.created_date <= #{createdDateEnd}
and t1.enabled=1
and t1.is_back=0
