mapper.select:
SELECT
depot.office_id officeId,depot.area_id areaId,t1.*
FROM
crm_ad_goods_order t1,crm_depot depot
WHERE
t1.enabled=1
and t1.shop_id=depot.id

    and t1.bill_type =#{p.billType}


    and t1.process_status =#{p.processStatus}


    and t1.remarks like concat('%',#{p.remarks},'%')


    and t1.created_date >= #{p.createdDateStart}


    and t1.created_date <= #{p.createdDateEnd}


    and t1.bill_date >= #{p.billDateStart}


    and t1.bill_date <= #{p.billDateEnd}








    AND t1.store_id =#{p.storeId}
    )





    and t1.shop_id in(
    select depot.id
    from crm_depot depot
    where depot.area_type like concat('%',#{p.areaType},'%')
    and depot.enabled=1
    )
