mapper.select:
SELECT
depot.office_id officeId,t1.*
FROM
crm_shop_build t1,crm_depot depot
WHERE
t1.enabled=1
and t1.shop_id=depot.id

    and t1.fixture_type=#{p.fixtureType}


    and t1.shop_id =#{p.shopId}


    and  t1.created_date >=#{p.createdDateStart}


    and  t1.created_date <#{p.createdDateEnd}


    and t1.process_position_id =#{p.positionId}


    and t1.created_by =#{p.createdBy}


    and depot.office_id = #{p.officeId}
